mapper.select:
SELECT
	u.*, up.gender, up.full_name, up.nick_name, up.address, up.address_detail, up.picture,
	up.birthd, up.agree_privacy_3party, up.agree_marketing, up.fcm_notice, up.fcm_group_notice
FROM groups u
JOIN group_profiles up ON u.id = up.group_id
WHERE u.removed = 0


		AND u.naver_id = #{socialId}


		AND u.kakao_id = #{socialId}


		AND u.apple_id = #{socialId}

mapper.insert:
		INSERT INTO groups (
			password, phone

			, naver_id


			, kakao_id


			, apple_id

		) VALUES (
			#{password}, #{phone}

			, #{naverId}


			, #{kakaoId}


			, #{appleId}

		)


        	SELECT LAST_INSERT_ID()

INSERT INTO group_profiles (
	group_id, full_name, nick_name, gender, birthd

	, picture


	, fcm_notice


	, fcm_group_notice


	, address


	, address_detail


	, agree_privacy_3party

) VALUES (
	#{id}, #{fullName}, #{nickName}, #{gender}, #{birthd}

	, #{picture}


	, #{fcmNotice}


	, #{fcmGroupNotice}


	, #{address}


	, #{addressDetail}


	, #{agreePrivacy3Party}

)

mapper.update:
UPDATE groups SET
	phone = #{phone}

	, role = #{role}


	, status = #{status}


	, password = #{newPassword}

WHERE id = #{id}

UPDATE group_profiles SET
	group_id = #{id},
	full_name = #{fullNaem}

	, gender = #{gender}


	, nick_name = #{nickName}


	, picture = #{picture}


	, birthd = #{birthd}


	, agree_privacy_3party =  #{agreePrivacy3Party}


	, fcm_notice = #{fcmNotice}


	, fcm_group_notice = #{fcmGroupNotice}


	, address = #{address}


	, address_detail = #{addressDetail}


WHERE group_id = #{id}
